-- NUXAR Perfume Database Migration SQL
-- Target: Supabase SQL Editor
-- This script will create a new 'perfumes' table and populate it with 66 items.

-- 1. DROP old table if exists (CAUTION: deletes old data)
DROP TABLE IF EXISTS perfumes;

-- 2. CREATE the new perfumes table
CREATE TABLE perfumes (
    id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    brand TEXT NOT NULL,
    full_name TEXT NOT NULL,
    year INTEGER,
    gender TEXT CHECK (gender IN ('pria', 'wanita', 'unisex')),
    category TEXT,
    concentration TEXT,
    notes JSONB,
    karakter TEXT,
    penggunaan TEXT,
    description TEXT,
    is_official BOOLEAN DEFAULT true,
    note TEXT,
    display_order INTEGER,
    created_at TIMESTAMPTZ DEFAULT NOW()
);

-- 3. INSERT Cowok/Unisex perfumes (from perfumeDataCowok.ts)

-- 4. INSERT Cewek perfumes (from perfumeDataCewek.ts)

-- 5. INDEXES for better search performance
CREATE INDEX IF NOT EXISTS idx_perfumes_name ON perfumes(name);
CREATE INDEX IF NOT EXISTS idx_perfumes_gender ON perfumes(gender);

-- 6. ENABLE Row Level Security
ALTER TABLE perfumes ENABLE ROW LEVEL SECURITY;

-- 7. CREATE POLICY to allow public read access
-- 'USING (true)' allows everyone (including anonymous users) to read the data.
CREATE POLICY "nuxar_perfumes_public_read" 
ON public.perfumes 
FOR SELECT 
TO public 
USING (true);
