DROP TABLE IF EXISTS public.perfumes CASCADE;
DROP TABLE IF EXISTS public.blogs CASCADE;

CREATE TABLE public.perfumes (
    id TEXT PRIMARY KEY,
    name TEXT NOT NULL,
    brand TEXT,
    full_name TEXT NOT NULL,
    gender TEXT,
    category TEXT,
    notes JSONB,
    character JSONB,
    personality JSONB,
    best_for JSONB,
    performance JSONB,
    additional JSONB,
    description TEXT,
    image_url TEXT,
    display_order INTEGER,
    created_at TIMESTAMPTZ DEFAULT NOW()
);

CREATE TABLE public.blogs (
    id TEXT PRIMARY KEY,
    slug TEXT UNIQUE NOT NULL,
    title TEXT NOT NULL,
    excerpt TEXT,
    content TEXT,
    read_time TEXT,
    date TEXT,
    image TEXT,
    category TEXT,
    created_at TIMESTAMPTZ DEFAULT NOW()
);

